INSERT INTO TGMCMS_CONTENTS ( REG_USER_ID , REG_DATE , CONTENTS_TITLE , CATEGORY_CD , DOWN_YN , MEDIA_USE , DESCR , UPLOAD_TYPE
) SELECT #{lbRegUserId} , NOW() , #{lbTitle} , #{lbCategorySeq} , A.LB_VOD_DOWN_YN , A.LB_OPEN_YN , A.LB_DESC , 'G'
FROM TGLIVE_BROADCAST A JOIN TGLIVE_BROADCAST_FILE B ON A.LB_SEQ = B.LB_SEQ WHERE A.LB_SEQ = #{lbSeq}
SELECT LAST_INSERT_ID()

INSERT INTO TGMCMS_VIDEO( MEDIA_ID , ORIGINAL_FILE_PATH , ORIGINAL_FILE_NAME , ENCODING_FILE_PATH , ENCODING_FILE_NAME
, ENC_SIZE , STATUS , REG_DATE , DESCR , CATEGORY_CD , REG_USER_ID , DOWN_YN , CONTENTS_ID , PRESET
, MEDIA_USE , DURATION , WIDTH , HEIGHT ) SELECT #{lbSeq} , #{vodSavePath} , CONCAT(#{recordCopyName},'.mp4') , #{recordCopyPath} , CONCAT(#{recordCopyName},'.mp4')
, #{vodSize} , '2' , NOW() , A.LB_DESC , #{lbCategorySeq} , A.LB_REG_USER_ID , A.LB_VOD_DOWN_YN , #{contentsSeq} , #{presetData}
, 'Y' , #{lbjDuration} , #{vodWidth} , #{vodHeight} FROM TGLIVE_BROADCAST A JOIN TGLIVE_BROADCAST_FILE B ON A.LB_SEQ = B.LB_SEQ WHERE A.LB_SEQ = #{lbSeq}

