mapper.insert:
INSERT INTO TGLIVE_USER (
	  LU_ID
	, LU_NAME
	, LU_PWD
	, REG_DATE
	) VALUES (
	  #{luId}
	, #{luName}
	, #{luPwd}
	, NOW()
	)

mapper.update:
UPDATE TGLIVE_USER
SET LU_PWD = #{luPwd}
WHERE LU_ID = #{luId}

mapper.insert:
INSERT INTO TGLIVE_SETTING_CONNECTIP (
	  LSC_SEQ
	, LSC_IP
	, LSC_REG_DATE
	, LSC_DESC
) VALUES (
	  CONCAT(DATE_FORMAT(NOW(), '%Y'),LPAD(nextval(sequence_lsc_16), 16, '0'))
	, #{lscIp}
	, NOW()
	, #{lscDesc}
)

mapper.update:
UPDATE TGLIVE_SETTING_CONNECTIP
SET  LSC_IP = #{lscIp}
	,LSC_DESC = #{lscDesc}
WHERE LSC_SEQ = #{lscSeq}
