mapper.insert:
INSERT INTO TGLIVE_BANNER (
	  BANNER_SEQ
	, BANNER_IMG_FILE_PATH
	, BANNER_IMG_FILE_NAME
	, BANNER_IMG_FILE_NAME_SERVER
	, BANNER_SHORTCUT_URL
	, BANNER_ORDER
	, REG_USER_ID
	, REG_DATE
) VALUES (
	  CONCAT(TO_CHAR(SYSDATE, 'YYYYMMDD'), LPAD(SEQUENCE_BANNER_12.NEXTVAL, 12, '0'))
	, #{bannerImgFilePath}
	, #{bannerImgFileName}
	, #{bannerImgFileNameServer}
	, #{bannerShortcutUrl}
	, #{bannerOrder}
	, #{regUserId}
	, SYSDATE
)

mapper.update:
UPDATE TGLIVE_BANNER
	SET 
		  DEL_YN = #{delYn}
		, DEL_USER_ID = #{delUserId}
		, DEL_DATE = SYSDATE
	WHERE 1 = 0

		OR BANNER_SEQ = #{bannerSeq}

UPDATE TGLIVE_BANNER
	SET 
	  BANNER_SHORTCUT_URL = #{bannerShortcutUrl}
	, MOD_USER_ID = #{modUserId}
	, MOD_DATE = SYSDATE
WHERE 1 = 0

	OR BANNER_SEQ = #{bannerSeq}
