SELECT Z.LB_SEQ , B.LB_TITLE , NVL(SUM(CASE WHEN MONITOR_DATE = #{date1} THEN ${viewsType} END),0) DATE1 , NVL(SUM(CASE WHEN MONITOR_DATE = #{date2} THEN ${viewsType} END),0) DATE2 , NVL(SUM(CASE WHEN MONITOR_DATE = #{date3} THEN ${viewsType} END),0) DATE3 , NVL(SUM(CASE WHEN MONITOR_DATE = #{date4} THEN ${viewsType} END),0) DATE4 , NVL(SUM(CASE WHEN MONITOR_DATE = #{date5} THEN ${viewsType} END),0) DATE5 , NVL(SUM(CASE WHEN MONITOR_DATE = #{date6} THEN ${viewsType} END),0) DATE6 , NVL(SUM(CASE WHEN MONITOR_DATE = #{date7} THEN ${viewsType} END),0) DATE7 FROM ( SELECT YEAR || '-' || MONTH || '-' || DAY || ' ' || HOUR || ':' || MINUTE AS MONITOR_DATE , LB_SEQ , ${viewsType} FROM TGLIVE_MONITOR_VIEWS WHERE (YEAR|| '-' || MONTH || '-' || DAY || ' ' || HOUR || ':' || MINUTE) IN
#{item}
) Z JOIN TGLIVE_BROADCAST B ON Z.LB_SEQ = B.LB_SEQ AND B.LB_STATUS = '1'
AND B.LB_CATEGORY_SEQ = #{categorySeq}
GROUP BY Z.LB_SEQ, B.LB_TITLE ORDER BY Z.LB_SEQ
